
public class CosenoDeAngulos {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int angulo = 0;
		for(int i=0; i <= 90; i += 5) {
			
			System.out.println("Coseno de " + angulo + " es: " + Math.cos(i));
			angulo += 5;
		}
		
		
		
		
		
	}

}
